mapper.insert:
INSERT INTO USER_FONTS (
    USER_ID, FONT_FAMILY, FONT_URL, IS_CUSTOM, CREATED_AT
) VALUES (
    #{user.userId}, #{fontFamily}, #{fontUrl}, #{isCustom}, SYSDATE
)

mapper.update:
UPDATE USER_FONTS 
SET 
    FONT_FAMILY = #{fontFamily},
    FONT_URL = #{fontUrl},
    IS_CUSTOM = #{isCustom}
WHERE FONT_ID = #{fontId}
